<?php

/**
* CryopreservationTable
* 
* This class has been auto-generated by the Doctrine ORM Framework
*/
class CryopreservationTable extends Doctrine_Table {
	/**
	* Returns an instance of this class.
	*
	* @return object CryopreservationTable
	*/
	public static function getInstance() {
		return Doctrine_Core::getTable('Cryopreservation');
	}
	
	public function findByTerm($term = '') {
		if ( preg_match('/^(BEA)?0*([1-9]\d*)/', $term, $matches) ) {
			return $this->createQuery('p')
				->leftJoin('p.Sample sa')
				->leftJoin('p.Strain st')
				->where('sa.id LIKE ?', "%{$matches[2]}%")
				->orWhere('st.id LIKE ?', "%{$matches[2]}%")
				->execute();
		}
		else {
			return $this->createQuery('p')->execute();
		}
	}
}