class DnaSequence extends BaseDnaSequence {
	public function getFormattedWorked() {
		if ( $this->_get('worked') == 0 ) {
			return 'no';
		}
		else {
			return 'yes';
		}
	}
	
	public function getNbReactions() {
		return Doctrine_Query::create()
			->from('PcrReaction r')
			->where('r.dna_sequence_id = ?', $this->getId())
			->count();
	}
}
