 */
class Isolator extends BaseIsolator {
	public function __toString() {
		return $this->getName().' '.$this->getSurname();
	}

	public function getFullName() {
		return $this->__toString();
	}

	public function getNbStrains() {
		return Doctrine_Query::create()
			->from('StrainIsolators s')
			->where('s.isolator_id = ?', $this->getId())
			->count();
	}

	public function getNbExternalStrains() {
		return Doctrine_Query::create()
			->from('ExternalStrainIsolators s')
			->where('s.isolator_id = ?', $this->getId())
			->count();
	}

	public function getNbPatentDeposits() {
		return Doctrine_Query::create()
			->from('PatentDepositIsolators s')
			->where('s.isolator_id = ?', $this->getId())
			->count();
	}

	public function getNbMaintenanceDeposits() {
		return Doctrine_Query::create()
			->from('MaintenanceDepositIsolators s')
			->where('s.isolator_id = ?', $this->getId())
			->count();
	}

}