<?php

/**
 * Location
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 *
 * @package    bna_green_house
 * @subpackage model
 * @author     Eliezer Talon <elitalon@inventiaplus.com>
 * @version    SVN: $Id: Builder.php 7691 2011-02-04 15:43:29Z jwage $
 */
class Location extends BaseLocation {

	public function getNbSamples() {
		if ( $samples = $this->getSamples() ) {
			return count($samples);
		}
		else {
			return Doctrine_Query::create()
				->from('Sample s')
				->where('s.location_id = ?', $this->getId())
				->count();
		}
	}

	public function getNbPictures() {
		return Doctrine_Query::create()
			->from('LocationPicture lp')
			->where('lp.location_id = ?', $this->getId())
			->count();
	}

	public function getNbStrains() {
		return LocationTable::getInstance()->createQuery('l')
			->select('count(DISTINCT st.id) AS n_strains')
			->leftJoin('l.Samples sa')
			->leftJoin('sa.Strains st')
			->where('l.id = ?', $this->getId())
			->fetchOne()
			->n_strains;
	}

	public function getGPSCoordinates() {
		return array(
			'latitude' => $this->getLatitude(),
			'longitude' => $this->getLongitude(),
		);
	}

	public function getFormattedGPSCoordinates() {
		$coordinates = $this->getGPSCoordinates();

		if ( $coordinates['latitude'] === null || $coordinates['longitude'] === null ) {
			return sfConfig::get('app_no_data_message');
		}
		elseif ( $coordinates['latitude'] === '' || $coordinates['longitude'] === '' ) {
			return sfConfig::get('app_no_data_message');
		}

		return $coordinates['latitude'].', '.$coordinates['longitude'];
	}

	public function getFormattedCategory() {
		$category = $this->getCategory();
		if ( $category->exists() ) {
			return $category->getName();
		}
		return sfConfig::get('app_no_data_message');
	}

}
